CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `view_user_roles` AS select `u`.`user_id` AS `user_id`,`u`.`username` AS `username`,`u`.`email` AS `email`,`r`.`role_name` AS `role_name`,`ur`.`assigned_at` AS `assigned_at` from ((`users` `u` join `user_roles` `ur` on((`u`.`user_id` = `ur`.`user_id`))) join `roles` `r` on((`ur`.`role_id` = `r`.`role_id`)))